CREATE MATERIALIZED VIEW "finances"."account_category_expenses_stats" WITH (autovacuum_enabled = true) AS (select "accountId", "categoryId", SUM(CASE WHEN "amount" > 0 THEN "amount" ELSE 0 END)::numeric as "income", SUM(CASE WHEN "amount" < 0 THEN abs("amount") ELSE 0 END)::numeric as "outcome" from "finances"."transactions" group by "finances"."transactions"."accountId", "finances"."transactions"."categoryId");